import React from 'react';
import './App.css';
import Form from './components/Form'
import EnterUID from './components/uid';
import {
  InfoWindow,
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
} from "react-google-maps";
import Geocode from "react-geocode";
import { Descriptions, Badge } from 'antd';
import AutoComplete from "react-google-autocomplete";




Geocode.setApiKey("AIzaSyBsPi3wA_-SZVURg6_iBq8zz5mxW5UcHNo");  

class App extends React.Component {
  state = {
    address: "",
    condensedAddress: "",
    coordinates: [],
    placeId: "",
    zoom: 12,
    height: 400,
    mapPosition: {
      lat: 0,
      lng: 0,
    },
    markerPosition: {
      lat: 0,
      lng: 0,
    }
  }



  onMarkerDragEnd = (event) => {
    let newLat = event.latLng.lat();
    let newLng = event.latLng.lng();

    Geocode.fromLatLng(newLat, newLng).then(response => {
      console.log('response', response);
      const address = response.results[0].formatted_address;
      const id = response.results[0].place_id;
      const lat = response.results[0].geometry.location.lat;
      const lng = response.results[0].geometry.location.lng;
      const coordinates = [lat, lng];

      this.setState({
        address: (address) ? address : "",
        condensedAddress: (address) ? address : "",
        coordinates: (coordinates) ? coordinates : "",
        placeId: (id) ? id : "",
        mapPosition: {
          lat: lat,
          lng: lng
        },
        markerPosition: {
          lat: lat,
          lng: lng
        }
      })
    });

  }

  render() {
    const MapWithAMarker = withScriptjs(withGoogleMap(props =>
      <GoogleMap
        defaultZoom={11}
        defaultCenter={{ lat: this.state.mapPosition.lat, lng: this.state.mapPosition.lng }}
      >
        <Marker
          draggable={true}
          onDragEnd={this.onMarkerDragEnd}
          position={{ lat: this.state.markerPosition.lat, lng: this.state.mapPosition.lng }}
        >
          <InfoWindow>
            <div>{this.state.address}</div>
          </InfoWindow>
        </Marker>

        <AutoComplete
          style={{ width: "100%", height: '40px', paddindLeft: 16, marginTop: 2, marginBottom: '2rem' }}
          types={['{address}']}
          onPlaceSelected={(place) => {
            console.log(place);
          }}
        />


      </GoogleMap>
    ));

    return (
      <div>
      <MapWithAMarker
        googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyBsPi3wA_-SZVURg6_iBq8zz5mxW5UcHNo&v=3.exp&libraries=geometry,drawing,places"
        loadingElement={<div style={{ height: `100%` }} />}
          containerElement={<div style={{ height: `400px` }} />}
          mapElement={<div style={{ height: `100%` }} />}
        />



        <Descriptions title="Location Infomation" bordered>
          <Descriptions.Item label="Address">{this.state.address}</Descriptions.Item>
          <Descriptions.Item label="Latitude">{this.state.coordinates[0]}</Descriptions.Item>
          <Descriptions.Item label="Longtitude">{this.state.coordinates[1]}</Descriptions.Item>
          <Descriptions.Item label="Place ID">{this.state.placeId}</Descriptions.Item>

        </Descriptions>




      </div>





    );
  }
}

export default App;





